"use strict";

var path = require('path');

var uniq = require('lodash/uniq');

var resolveFrom = require('resolve-from');

var dynamicRequire = require('./dynamicRequire');

var getSanityVersions = basePath => {
  var manifestPath = path.join(basePath, 'package.json');
  var pkg;

  try {
    pkg = dynamicRequire(manifestPath);
  } catch (err) {
    throw new Error(`Could not load package.json from ${manifestPath}`);
  }

  var dependencies = Object.keys(Object.assign({}, pkg.dependencies, pkg.devDependencies));
  var sanityDeps = dependencies.filter(depName => depName.indexOf('@sanity/') === 0);
  var versions = uniq(sanityDeps).reduce((target, moduleId) => {
    var modulePath = resolveFrom.silent(basePath, path.join(moduleId, 'package.json'));
    target[moduleId] = modulePath && dynamicRequire(modulePath).version;
    return target;
  }, {});
  return versions;
};

module.exports = getSanityVersions;