"use strict";

var inferFromSchemaType = require('./inferFromSchemaType'); // Note: Mutates schema. Refactor when @sanity/schema supports middlewares


function inferFromSchema(schema) {
  var typeNames = schema.getTypeNames();
  typeNames.forEach(typeName => {
    inferFromSchemaType(schema.get(typeName), schema);
  });
  return schema;
}

module.exports = inferFromSchema;