"use strict";

var ValidationError = require('../ValidationError');

var genericValidator = require('./genericValidator');

var metaKeys = ['_key', '_type', '_weak'];

var presence = (expected, value, message) => {
  if (expected !== 'required') {
    return true;
  }

  var keys = value && Object.keys(value).filter(key => !metaKeys.includes(key));

  if (typeof value === 'undefined' || keys && keys.length === 0) {
    return new ValidationError(message || 'Required');
  }

  return true;
};

var reference = (unused, value, message) => {
  if (!value) {
    return true;
  }

  if (typeof value._ref !== 'string') {
    return new ValidationError(message || 'Must be a reference to a document', {
      paths: ['$']
    });
  }

  return true;
};

var assetRequired = (flag, value, message) => {
  if (!value || !value.asset || !value.asset._ref) {
    var assetType = flag.assetType || 'Asset';
    return new ValidationError(message || "".concat(assetType, " required"));
  }

  return true;
};

module.exports = Object.assign({}, genericValidator, {
  presence,
  reference,
  assetRequired
});